mapper.select:
select bno, title, contents, writer, regDate, editDate
from bbs
where bno = #{bno}

mapper.update:
update bbs
set title = #{title}, contents = #{contents}, writer = #{writer}, editDate = (select now())
where bno = #{bno}

mapper.delete:
update bbs
set delYn = 'Y'
where bno = #{bno}

mapper.select:
select * from bbs where (
title like concat('%',#{keyword},'%') or contents like concat('%',#{keyword},'%') or writer like concat('%',#{keyword},'%')
${search_option} like concat('%',#{keyword},'%')
) and delYn = 'N' order by bno desc limit #{startNum}, #{postNum}
